query:
SELECT
?number_of_citing_works
?author ?authorLabel (?author as ?hiddenId)
?organization ?organizationLabel
?example_work ?example_workLabel
WITH {
SELECT DISTINCT ?author WHERE {
?author wdt:P27 | wdt:P1416/wdt:P17 | wdt:P108/wdt:P17 wd:{{ q }} .
}
ORDER BY ?author
LIMIT 1000
OFFSET {w}
} AS %authors
WITH {
SELECT
?author
(COUNT(DISTINCT ?citing_work) AS ?number_of_citing_works)
(SAMPLE(?organization_) AS ?organization)
(SAMPLE(?work) AS ?example_work)
WHERE {
INCLUDE %authors
?work wdt:P50 ?author .
OPTIONAL { ?citing_work wdt:P2860 ?work . }
OPTIONAL {
?author wdt:P1416 | wdt:P108 ?organization_ .
?organization_ wdt:P17 wd:{{ q }}
}
}
GROUP BY ?author
} AS %results
WHERE {
INCLUDE %results
FILTER(?number_of_citing_works > 10)
service wikibase:label { bd:serviceParam wikibase:language "en" . }
}
ORDER BY DESC(?number_of_citing_works)

count_query:
SELECT (COUNT(DISTINCT ?author) as ?subsetting_size) WHERE {
?author wdt:P27 | wdt:P1416/wdt:P17 | wdt:P108/wdt:P17 wd:{{ q }} .
}

query:
SELECT
?number_of_authors
?number_of_works
?organization ?organizationLabel
WITH {
SELECT
?organization
WHERE {
?organization wdt:P17 wd:{{ q }} .
}
ORDER BY ?organization
LIMIT 1000
OFFSET {w}
} AS %orgs
WITH {
SELECT
?organization
(COUNT(DISTINCT ?author) AS ?number_of_authors)
(COUNT(DISTINCT ?work) AS ?number_of_works)
WHERE {
INCLUDE %orgs
?author wdt:P108 | wdt:P463 | wdt:P1416/wdt:P361* ?organization .
?work wdt:P50 ?author .
}
GROUP BY ?organization
} AS %results
WHERE {
INCLUDE %results
SERVICE wikibase:label { bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en,da,es,fr,jp,nl,no,ru,sv,zh". }
}
ORDER BY DESC(?number_of_authors) DESC(?number_of_works)

count_query:
SELECT (COUNT(DISTINCT ?org) as ?subsetting_size) WHERE {
?org wdt:P17 wd:{{ q }} .
}

query:
SELECT
?location ?locationLabel
?work ?workLabel
?geo
?layer
WITH {
SELECT ?location ?work ?geo ?type WHERE {
?location wdt:P17 wd:{{ q }} .
?location wdt:P159? / wdt:P625 ?geo.
?work wdt:P921 ?location .
FILTER NOT EXISTS { ?work wdt:P31 wd:Q17329259 }
}
ORDER BY ?work
LIMIT 1000
OFFSET {w}
} AS %results
WHERE {
INCLUDE %results
OPTIONAL {
?work wdt:P31 / rdfs:label ?layer .
FILTER (LANG(?layer) = 'en')
}
SERVICE wikibase:label { bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en,da,de,es,fr,jp,nl,no,ru,sv,zh". }
}

count_query:
