query:
SELECT
?number_of_authors
?number_of_works
?organization ?organizationLabel
WITH {
SELECT
?organization
WHERE {
?organization wdt:P17 wd:{{ q }} .
}
ORDER BY ?organization
LIMIT 1000
OFFSET {w}
} AS %orgs
WITH {
SELECT
?organization
(COUNT(DISTINCT ?author) AS ?number_of_authors)
(COUNT(DISTINCT ?work) AS ?number_of_works)
WHERE {
INCLUDE %orgs
?author wdt:P108 | wdt:P463 | wdt:P1416/wdt:P361* ?organization .
?work wdt:P50 ?author .
}
GROUP BY ?organization
} AS %results
WHERE {
INCLUDE %results
SERVICE wikibase:label { bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en,da,es,fr,jp,nl,no,ru,sv,zh". }
}
ORDER BY DESC(?number_of_authors) DESC(?number_of_works)

count_query:
SELECT (COUNT(DISTINCT ?org) as ?subsetting_size) WHERE {
?org wdt:P17 wd:{{ q }} .
}

query:
SELECT
?location ?locationLabel
?work ?workLabel
?geo
?layer
WITH {
SELECT ?location ?work ?geo ?type WHERE {
?location wdt:P17 wd:{{ q }} .
?location wdt:P159? / wdt:P625 ?geo.
?work wdt:P921 ?location .
FILTER NOT EXISTS { ?work wdt:P31 wd:Q17329259 }
}
ORDER BY ?location
LIMIT 1000
OFFSET {w}
} AS %results
WHERE {
INCLUDE %results
OPTIONAL {
?work wdt:P31 / rdfs:label ?layer .
FILTER (LANG(?layer) = 'en')
}
SERVICE wikibase:label { bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en,da,de,es,fr,jp,nl,no,ru,sv,zh". }
}

